import React, { useState } from 'react';
import { View, Text, StyleSheet,SafeAreaView, StatusBar, 
  TouchableOpacity, FlatList, Modal, TextInput } from 'react-native';
import {Ionicons} from '@expo/vector-icons'; 
import TaskList from './src/components/TaskList'
import * as Animatable from 'react-native-animatable';

const AnimatedBtn = Animatable.createAnimatableComponent(TouchableOpacity); 


export default function App() {
    const[task, setTask] = useState([
      {key:1, task: 'Comprar pao'}, 
      {key:2, task: 'Estudar React Native'}, 
      {key:3, task: 'Academia'}, 
      {key:4, task: 'Comprar chocolate'}, 
      {key:5, task: 'Ver video'}, 
    ]); 
    const[open, setOpen] = useState(false);


  return (
    <SafeAreaView style={styles.container}>

      <StatusBar backgroundColor="#171d31" barStyle="light-content"/>

      <View style={styles.content}> 
        <Text style={styles.title}>Minhas Tarefas</Text>
      </View>


      <AnimatedBtn 
      style={styles.fab}
      animation="bounceInUp"
      duration={1500}
      useNativeDriver
      onPress={ () => setOpen(true) }
      >
        <Ionicons name='ios-add' size={35} color = '#fff'/>
      </AnimatedBtn>
    </SafeAreaView>

  );
}

const styles = StyleSheet.create ({
  container: {
    flex:1,
    backgroundColor: '#171d31'
  },
  title: {
    marginTop: 10,
    paddingBottom:10,
    fontSize: 30,
    textAlign: 'center',
    color:'#FFFfff'
  },
  fab:{
    position: 'absolute',
    width: 60,
    height: 60,
    backgroundColor: "#0094ff",
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 30,
    right: 25,
    bottom: 25,
    elevation: 2, //da sombra
    zIndex: 9,     // deixa acima
    shadowColor: '#000',
    shadowOpacity: 0.2,
    shadowOffset: { //dentro vai um objeto
      width: 1,
      height: 3,
    }
  }
});
